package org.example.service.rollbackChore;import org.apache.rocketmq.spring.annotation.RocketMQMessageListener;import org.apache.rocketmq.spring.core.RocketMQListener;import org.example.grpc.server.AccountChoreGrpcServer;import org.example.model.AccChore;import org.example.service.AccountService;import org.springframework.stereotype.Service;@RocketMQMessageListener(topic = "accountRollbackTopic", consumerGroup = "accountService-consumer-group")@Servicepublic class AccountRollback implements RocketMQListener<AccChore> {    private AccountChoreGrpcServer accountChoreGrpcServer;    public AccountRollback(AccountChoreGrpcServer accountChoreGrpcServer) {        this.accountChoreGrpcServer = accountChoreGrpcServer;    }    @Override    public void onMessage(AccChore accChore) {        accountChoreGrpcServer.rollbackAgent(accChore.getRollbackId());    }}