package org.example.repository;import com.baomidou.mybatisplus.core.mapper.BaseMapper;import org.apache.ibatis.annotations.Mapper;import org.apache.ibatis.annotations.Param;import org.apache.ibatis.annotations.Select;import org.apache.ibatis.annotations.Update;import org.example.commonModels.AgentChore;@Mapperpublic interface AgentChoreDao extends BaseMapper<AgentChore> {    @Select("SELECT MAX(id) FROM AgentChore")    Long selectMaxId();    @Update("ALTER TABLE AgentChore AUTO_INCREMENT = #{autoIncrementValue}")    void resetAutoIncrement(@Param("autoIncrementValue") Long autoIncrementValue);}