package org.example.config;import org.example.interfaces.SagaSteps;import org.springframework.stereotype.Service;import java.util.ArrayList;import java.util.List;@Servicepublic class SagaOrchestrator {    private static List<SagaSteps> steps = new ArrayList<>();    public static void addStep(SagaSteps step) {        steps.add(step);    }    public static void execute() {        try {            for (SagaSteps step : steps) {                step.execute();            }        } catch (Exception e) {            compensate();            throw new RuntimeException("Saga execution failed", e);        }    }    private static void compensate() {        for (int i = steps.size() - 1; i >= 0; i--) {            steps.get(i).compensate();        }    }}