package org.example.controller;import org.example.commonModels.UserChore;import org.example.service.UserService;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RestController;@RestControllerpublic class UserContrllerChore {    private UserService userService;    public UserContrllerChore(UserService userService) {        this.userService = userService;    }    @PostMapping("user/saveChore")    public boolean saveChor(@RequestBody UserChore userChore){       return userService.createUserChore(userChore);    }}