package org.example.repository;import com.baomidou.mybatisplus.core.mapper.BaseMapper;import org.apache.ibatis.annotations.Mapper;import org.apache.ibatis.annotations.Param;import org.apache.ibatis.annotations.Select;import org.apache.ibatis.annotations.Update;import org.example.commonModels.UserChore;@Mapperpublic interface UserChoreDao extends BaseMapper<UserChore> {    @Select("SELECT MAX(id) FROM UserChore")    Long selectMaxId();    @Update("ALTER TABLE UserChore AUTO_INCREMENT = #{autoIncrementValue}")    void resetAutoIncrement(@Param("autoIncrementValue") Long autoIncrementValue);}