package org.example.service;import org.example.interfaces.SagaSteps;import org.example.model.Author;import org.example.repository.AuthorDao;public class CreateAuthor implements SagaSteps {    private final AuthorDao authorDao;    private final Author author;    public CreateAuthor(AuthorDao authorDao, Author author) {        this.authorDao = authorDao;        this.author = author;    }    @Override    public void execute() {        authorDao.insert(author);    }    @Override    public void compensate() {        authorDao.deleteById(author.getId());    }}