package org.example.service.rollback;import org.apache.rocketmq.spring.annotation.RocketMQMessageListener;import org.apache.rocketmq.spring.core.RocketMQListener;import org.example.commonModels.UserChore;import org.example.service.UserService;import org.springframework.stereotype.Service;@RocketMQMessageListener(topic = "UserRollbackTopic", consumerGroup = "userService-consumer-group")@Servicepublic class UserRollback implements RocketMQListener<UserChore> {    private UserService userService;    public UserRollback(UserService userService) {        this.userService = userService;    }    @Override    public void onMessage(UserChore userChore) {        userService.rollbackUser(userChore.getRollbackId());    }}